import "./CadastroLivro"; 
import axios from "axios";
import Aside from "../layout/Aside"; 
import {useState, useEffect } from "react"
import Select from "react-select"

const selectStyles = {
    control: (baseStyles, state) => ({
      ...baseStyles,
      margin: 0,
      padding: "5px 0",
      borderRadius: 3,
      borderColor: "gray",
      boxShadow: state.isFocused ? "0 0 0 2px black" : 0,
      ":hover": { borderColor: "black" },
    }),
  };

  function CadastroLeitor(){
    const [livros, setLivros] = useState([]);
    const [livrosSelecionados, setLivrosSelecionados] = useState(); 
    const [leitor, setLeitor] = useState(null); 
    const [leitores, setLeitores] = useState([]); 

    function getLeitores(){
        axios.get("http://localhost:3005/leitores").then((resposta) =>
        {
            setLeitores(resposta.data);
        });
    }

    function getLivros(){
        axios.get("http://localhost:3005/livros").then((resposta) =>
             { 
                setLivros(resposta.data);
              });
        }

    useEffect(() => {
            getLivros();
            getLeitores();
       }, [])

    function novoLeitor(){
        setLeitor({
            // _id:"",
            nomeLeitor: "", 
            idadeLeitor: "",
            livro: [],
        })
    }
    function alterarLeitor(campo, valor, id){
        leitor[campo] = valor; 
        setLeitor({
            _id: id,
            ...leitor,
        }); 
    }
       
    function excluirLeitor(id){
        axios.delete("http://localhost:3005/leitores/" + id).then(() => {
            reiniciarEstadoDosObjetos(); 
        }); 
    }
    
    function salvarLeitor() {
        if(leitor._id) {
            axios.put("http://localhost:3005/leitores/" + leitor._id, leitor).then(() => { reiniciarEstadoDosObjetos();
             });
        }else{
            axios.post("http://localhost:3005/leitores/", leitor).then(() => {
                reiniciarEstadoDosObjetos();
            })
        }
    }

    
function getSelectLivros() {
    if (leitor !== null) {
      const vetLeitores = [];
      const leitoresAntes = [];
  
      for (let i = 0; i < leitores.length; i++) {
        const leitor = leitores[i];
        if (livro.leitores && livro.leitores.includes(leitor._id)) {
          leitoresAntes[i] = {
            value: leitor._id,
            label: leitor.nomeLeitor,
          };
        }
  
        vetLeitores[i] = {
          value: leitor._id,
          label: leitor.nomeLeitor,
        };
      }
  
      return (
        <Select
          isMulti
          isClearable={false}
          value={leitoresSelecionados}
          defaultValue={leitoresAntes}
          onChange={onChangeSelectLeitores}
          options={vetLeitores}
          styles={selectStyles}
        />
      );
    }
  
    return null;
  }
  










  }  
  export default CadastroLeitor;
